using System;
using System.IO;

namespace ImageNoter.Tests.Resources;

public static class TestImage
{
    // This is a minimal JPEG with EXIF data
    private const string Base64Image = "";

    public static string CreateTestImageWithExif()
    {
        var testImagePath = Path.Combine(Path.GetTempPath(), $"test_with_exif_{Guid.NewGuid()}.jpg");
        File.WriteAllBytes(testImagePath, Convert.FromBase64String(Base64Image));
        return testImagePath;
    }
} 